import axios from "axios";
import { useState } from "react";

const ADDR = "http://localhost:5000/api/";

function getPokemonData(name, setter) {
  const send_this = new FormData();
  send_this.append("name", name);

  axios(
    {
      method: "POST", 
      url: ADDR, 
      data: send_this, 
      headers: {
        "Content-Type": "multipart/form-data"
      }
    }
  ).then((response) => {
    console.log(response.data.data[0]);
    setter(response);
  }).catch((error) => {
    console.log(error.message);
  });
}

function App() {
  const [data, setData] = useState({name: ""});

  return (
    <div className="App">
      This is a react app
      Current Data: {data.name}
      <button onClick={(event) => {getPokemonData("charizard", setData)}}> Click me to update </button>
    </div>
  );
}

export default App;
