    //   }
    // ).catch((error) => {
    //   console.log(error.message);
    // });
  }

  return (
    <div className="App">
      This is a react app <br></br>
      Current Data: <br></br>
      Dex No.: {data.id} <br></br>
      Name: {data.name} <br></br>
      Types: {data.type} <br></br>
      Image: <img src={data.image} alt={data.name}></img> <br></br>
      <form id="main" onSubmit={(event) => {updateButtonCallback(event)}}>
        <input className="name_inp" placeholder="Enter pokemon name here..."></input>
        <button type="submit"> Click me to update </button>
      </form>
    </div>
  );
}

export default App;
