import axios from "axios";
import { useState } from "react";
import ImageFrame from "./components/ImageFrame/ImageFrame";
import InputFrame from "./components/InputFrame/InputFrame";
import RandomImage from "./components/ImageFrame/frame.jpeg";

const ADDR = "http://localhost:5000/api/";
const IMAGEFRAMESTYLE=
  ` 
    min-h-80 
    min-w-80 
    flex 
    justify-center 
  `;
const INPUTFRAMESTYLE=
  ` 
    min-h-80 w-[100%] 
    min-w-50 
    lg:min-w-50 
    p-7 
    rounded-xl 
    rounded-t-none 
    lg:rounded-l-none 
    flex 
    flex-col 
    items-start 
    justify-center 
    bg-[#ff3334] 
    font-sans
  `
const TEXTINPUTSTYLE=
  `
  p-4 
  rounded-md
  h-10
  placeholder:italic
  text-neutral-700

  `
const BUTTONSTYLE=
  `
    bg-white 
    text-black 
    p-3 
    mt-3 
    rounded-md 
    bg-lime-400
    hover:bg-white
    transition-colors 
  `
const POKEMONIMAGESTYLE=
  `
    size-20
  `

function getPokemonData(name) {
  const send_this = new FormData();
  send_this.append("name", name);

  return new Promise((resolve, reject) => {
    axios(
      {
        method: "POST", 
        url: ADDR, 
        data: send_this, 
        headers: {
          "Content-Type": "multipart/form-data"
        }
      }
    ).then((response) => {
      // console.log(response.data.data[0]);
      resolve(response.data.data[0]);
    }).catch((error) => {
      // console.log(error.message);
      reject(error);
    });
  });
}



function App() {

  const [data, setData] = useState({name: ""});

  function updateButtonCallback(event) {
    event.preventDefault();
    const name = (event.target.getElementsByClassName("name_inp")[0].value).toLowerCase();
    // getPokemonData(name).then(
    //   (returned_val) => {
    //     setData(returned_val);
    //   }
    // ).catch((error) => {
    //   console.log(error.message);
    // });
    setData({
      id: 1, 
      name: "bulbasaur", 
      type: "grass, poison", 
      image: RandomImage
    });
  }

  return (
    <div className="App bg-zinc-900 text-white flex flex-column items-center justify-center min-h-screen">

      <div className="Frames flex flex-col lg:flex-row items-center justify-center rounded-2xl w-2/3">
        <div className="Spacer flex-grow w-[100%]"></div>
        <ImageFrame className={"ImageFrame" + IMAGEFRAMESTYLE} data={data} pokemonimagestyle={POKEMONIMAGESTYLE}/>
        <InputFrame className={"InputFrame" + INPUTFRAMESTYLE} textinputstyle={TEXTINPUTSTYLE} buttonstyle={BUTTONSTYLE} data={data} updateButtonCallback={updateButtonCallback}/>
        <div className="Spacer flex-grow w-[100%]"></div>
      </div>

    </div>
  );
}

export default App;
