import axios from "axios";
import { useState } from "react";
import ImageFrame from "./components/ImageFrame/ImageFrame";
import InputFrame from "./components/InputFrame/InputFrame";

const ADDR = "http://localhost:5000/api/";

function getPokemonData(name) {
  const send_this = new FormData();
  send_this.append("name", name);

  return new Promise((resolve, reject) => {
    axios(
      {
        method: "POST", 
        url: ADDR, 
        data: send_this, 
        headers: {
          "Content-Type": "multipart/form-data"
        }
      }
    ).then((response) => {
      // console.log(response.data.data[0]);
      resolve(response.data.data[0]);
    }).catch((error) => {
      // console.log(error.message);
      reject(error);
    });
  });
}



function App() {

  const [data, setData] = useState({name: ""});

  function updateButtonCallback(event) {
    event.preventDefault();
    const name = (event.target.getElementsByClassName("name_inp")[0].value).toLowerCase();
    // getPokemonData(name).then(
    //   (returned_val) => {
    //     setData(returned_val);
    //   }
    // ).catch((error) => {
    //   console.log(error.message);
    // });
    setData({
      id: 1, 
      name: "bulbasaur", 
      type: "grass, poison", 
      image: ""
    });
  }

  return (
    <div className="App bg-zinc-900 text-white flex flex-column items-center justify-center min-h-screen">
      {/* Current Data: <br></br>
      Dex No.: {data.id} <br></br>
      Name: {data.name} <br></br>
      Types: {data.type} <br></br>
      Image: <img src={data.image} alt={data.name}></img> <br></br>
      <form id="main" onSubmit={(event) => {updateButtonCallback(event)}}>
        <input className="name_inp" placeholder="Enter pokemon name here..."></input>
        <button type="submit"> Click me to update </button>
      </form> */}
      <div className="Frames flex flex-row items-center justify-center">
        <ImageFrame className="ImageFrame size-3/12 flex justify-center border-2 border-dashed p-10 rounded-2xl"/>
        <InputFrame className="InputFrame size=3/12 flex flex-column items-center justify-around bg-[#ff3334] h-64" />
      </div>

    </div>
  );
}

export default App;
