import axios from "axios";
import { useState } from "react";

const ADDR = "http://localhost:5000/api/";

function getPokemonData(name) {
  const send_this = new FormData();
  send_this.append("name", name);

  return new Promise((resolve, reject) => {
    axios(
      {
        method: "POST", 
        url: ADDR, 
        data: send_this, 
        headers: {
          "Content-Type": "multipart/form-data"
        }
      }
    ).then((response) => {
      // console.log(response.data.data[0]);
      resolve(response.data.data[0]);
    }).catch((error) => {
      // console.log(error.message);
      reject(error);
    });
  });
}



function App() {

  const [data, setData] = useState({name: ""});

  function updateButtonCallback(name) {
    getPokemonData(name).then(
      (returned_val) => {
        setData(returned_val);
      }
    ).catch((error) => {
      console.log(error.message);
    });
  }

  return (
    <div className="App">
      This is a react app <br></br>
      Current Data: <br></br>
      Dex No.: {data.id} <br></br>
      Name: {data.name} <br></br>
      Types: {data.type} <br></br>
      Image: <img src={data.image} alt="charizard"></img> <br></br>

      <button onClick={(event) => {updateButtonCallback("charizard")}}> Click me to update </button>
    </div>
  );
}

export default App;
