import axios from "axios";
import { useState } from "react";
import ImageFrame from "./components/ImageFrame/ImageFrame";
import InputFrame from "./components/InputFrame/InputFrame";

const ADDR = "http://localhost:5000/api/";

function getPokemonData(name) {
  const send_this = new FormData();
  send_this.append("name", name);

  return new Promise((resolve, reject) => {
    axios(
      {
        method: "POST", 
        url: ADDR, 
        data: send_this, 
        headers: {
          "Content-Type": "multipart/form-data"
        }
      }
    ).then((response) => {
      // console.log(response.data.data[0]);
      resolve(response.data.data[0]);
    }).catch((error) => {
      // console.log(error.message);
      reject(error);
    });
  });
}



function App() {

  const [data, setData] = useState({name: ""});

  function updateButtonCallback(event) {
    event.preventDefault();
    const name = (event.target.getElementsByClassName("name_inp")[0].value).toLowerCase();
    // getPokemonData(name).then(
    //   (returned_val) => {
    //     setData(returned_val);
    //   }
    // ).catch((error) => {
    //   console.log(error.message);
    // });
    setData({
      id: 1, 
      name: "bulbasaur", 
      type: "grass, poison", 
      image: ""
    });
  }

  return (
    <div className="App bg-zinc-900 text-white flex flex-column items-center justify-center min-h-screen">

      <div className="Frames flex flex-row items-center justify-center border-2 border-dashed p-10 sm:p-0 rounded-2xl w-2/3">
        <div className="Spacer flex-grow w-[100%]"></div>
        <ImageFrame className="ImageFrame min-h-80 min-w-80 flex justify-center "/>
        <InputFrame className="InputFrame min-h-80 min-w-50 p-7 rounded-xl rounded-l-none flex flex-column items-center justify-around bg-[#ff3334]" data={data} updateButtonCallback={updateButtonCallback}/>
        <div className="Spacer flex-grow w-[100%]"></div>
      </div>

    </div>
  );
}

export default App;
