import axios from "axios";
import { useState } from "react";

const ADDR = "http://localhost:5000/api/";

function getPokemonData(name, setter) {
  axios(
    {
      method: "POST", 
      url: ADDR, 
      mydata: name
    }
  ).then((response) => {
    console.log(response);
    setter(response);
  }).catch((error) => {
    console.log(error.message);
  });
}

function App() {
  const [data, setData] = useState({name: ""});

  return (
    <div className="App">
      This is a react app
      Current Data: {data.name}
      <button onClick={(event) => {getPokemonData("charizard", setData)}}> Click me to update </button>
    </div>
  );
}

export default App;
